import json
import re
import yaml
from typing import Dict, List, Any

def extract_olmo_7b_scores(readme_content: str) -> Dict[str, float]:
    """Extract specific benchmark scores for OLMo 7B from the README table."""

    # Find the main evaluation table
    lines = readme_content.split('\n')
    table_data = []
    current_table = []
    in_table = False

    for line in lines:
        if '| Llama 7B' in line and 'archery' not in line:
            in_table = True

        if in_table and line.strip():
            current_table.append(line.strip())

        if in_table and not line.strip():
            if current_table:
                table_data.append(current_table)
            current_table = []
            in_table = False

    if current_table:
        table_data.append(current_table)

    # Parse the first evaluation table (OLMo 7B vs other models)
    main_table = table_data[0] if table_data else []

    # Extract column positions
    header = main_table[0].split('|')
    olmo_7b_idx = -1

    for i, col in enumerate(header):
        if 'OLMo 7B' in col and 'Twin' not in col:
            olmo_7b_idx = i
            break

    if olmo_7b_idx == -1:
        raise ValueError("OLMo 7B column not found in evaluation table")

    # Extract scores for required benchmarks
    required_benchmarks = [
        'arc_challenge', 'arc_easy', 'boolq', 'copa', 'hellaswag', 
        'openbookqa', 'piqa', 'sciq', 'winogrande', 'mmlu', 'truthfulqa'
    ]

    scores = {}

    for row in main_table[2:]:  # Skip header and separator
        columns = row.split('|')
        benchmark_name = columns[0].strip().lower()

        # Clean benchmark name for matching
        benchmark_clean = re.sub(r'\(.*\)', '', benchmark_name).strip()
        benchmark_clean = re.sub(r'[^a-zA-Z0-9]', '', benchmark_clean)

        for required in required_benchmarks:
            required_clean = re.sub(r'[^a-zA-Z0-9]', '', required)
            if benchmark_clean.startswith(required_clean):
                try:
                    score = float(columns[olmo_7b_idx].strip())
                    scores[required] = score
                    break
                except (ValueError, IndexError):
                    continue

    return scores

def create_model_index_yaml(scores: Dict[str, float]) -> Dict[str, List[Dict[str, Any]]]:
    """Create model-index YAML structure from scores."""

    metrics = []
    for benchmark, score in scores.items():
        metrics.append({
            'name': benchmark,
            'type': benchmark,
            'value': score
        })

    model_index = {
        'model-index': [
            {
                'name': 'OLMo-7B',
                'results': [
                    {
                        'task': {
                            'type': 'text-generation'
                        },
                        'dataset': {
                            'name': 'Core Evaluation Benchmarks',
                            'type': 'benchmark'
                        },
                        'metrics': metrics,
                        'source': {
                            'name': 'Model README',
                            'url': 'https://huggingface.co/allenai/OLMo-7B'
                        }
                    }
                ]
            }
        ]
    }

    return model_index

def main():
    # Read the README content from standard input or file
    # For now, let's use the hardcoded table data

    # Manual extraction from the table based on our earlier analysis
    scores = {
        'arc_challenge': 48.5,
        'arc_easy': 65.4,
        'boolq': 73.4,
        'copa': 90.0,
        'hellaswag': 76.4,
        'openbookqa': 50.2,
        'piqa': 78.4,
        'sciq': 93.8,
        'winogrande': 67.9,
        'mmlu': 28.3,  # From "MMLU (5 shot MC)"
        'truthfulqa': 36.0  # From "truthfulQA (MC2)"
    }

    print(f"Extracted {len(scores)} benchmark scores:")
    for benchmark, score in scores.items():
        print(f"  {benchmark}: {score}")

    # Create YAML structure
    yaml_data = create_model_index_yaml(scores)

    # Write to file
    output_file = '/home/ssmith/source/skills-test/olmo_7b_evaluations.yaml'
    with open(output_file, 'w') as f:
        yaml.dump(yaml_data, f, default_flow_style=False, sort_keys=False)

    print(f"\nYAML output written to {output_file}")
    print("\nPreview:")
    print(yaml.dump(yaml_data, default_flow_style=False, sort_keys=False))

if __name__ == "__main__":
    main()
