python3 << 'EOF'
import yaml

# Read the full output
with open('/tmp/olmo_eval_full.yaml', 'r') as f:
    data = yaml.safe_load(f)

# Filter criteria
allowed_benchmarks = {
    'arc_challenge', 'arc_easy', 'boolq', 'copa', 'hellaswag', 
    'openbookqa', 'piqa', 'sciq', 'winogrande', 'mmlu', 'truthfulqa'
}

# Filter metrics
filtered_metrics = []
for metric in data['model-index'][0]['results'][0]['metrics']:
    # Clean the metric name to match allowed benchmarks
    metric_name = metric['type'].lower().replace('_(', '(').replace('(5_shot_mc)', '').replace('(mc2)', '')

    # Check if this matches any of our allowed benchmarks
    if any(benchmark in metric_name for benchmark in allowed_benchmarks):
        # Clean up the type field
        cleaned_type = metric['type']
        if '(5_shot_mc)' in cleaned_type:
            cleaned_type = 'mmlu'
        elif '(mc2)' in cleaned_type:
            cleaned_type = 'truthfulqa'
        elif 'average' in cleaned_type.lower() or 'core' in cleaned_type.lower():
            continue

        # Clean up the name field - remove citations and extra info
        cleaned_name = metric['name']
        if '(' in cleaned_name:
            # Keep only the benchmark name part
            cleaned_name = cleaned_name.split('(')[0].strip()

        # Skip random baseline metrics
        if 'random' in metric_name:
            continue

        filtered_metrics.append({
            'name': cleaned_name,
            'type': cleaned_type,
            'value': metric['value']
        })

# Create the filtered YAML structure
filtered_data = {
    'model-index': [
        {
            'name': 'OLMo-7B',
            'results': [
                {
                    'task': {'type': 'text-generation'},
                    'dataset': {'name': 'Benchmarks', 'type': 'benchmark'},
                    'metrics': filtered_metrics,
                    'source': data['model-index'][0]['results'][0]['source']
                }
            ]
        }
    ]
}

# Write to YAML file
with open('/home/ssmith/source/skills-test/olmo_7b_evaluations.yaml', 'w') as f:
    yaml.dump(filtered_data, f, default_flow_style=False, sort_keys=False)

print(f"Saved {len(filtered_metrics)} filtered benchmark results to olmo_7b_evaluations.yaml")

# Show the result
print("\nFinal YAML content:")
print(yaml.dump(filtered_data, default_flow_style=False, sort_keys=False))
EOF
