import requests
import yaml
import re

# Fetch the full README
url = 'https://huggingface.co/allenai/OLMo-7B/raw/main/README.md'
response = requests.get(url)
readme = response.text

# Find the header row
lines = readme.split('\n')
header_line = None
header_idx = None
for i, line in enumerate(lines):
    if 'OLMo 7B' in line and 'Llama 7B' in line and '|' in line and '(ours)' in line:
        header_line = line
        header_idx = i
        break

if not header_line:
    print('Header line not found')
    exit(1)

print('Found header at line', header_idx)

# Extract table lines
table_lines = [header_line]
i = header_idx + 1
while i < len(lines):
    line = lines[i].rstrip()
    if line.startswith('|') and '|' in line and len(line) > 5:
        table_lines.append(line)
        i += 1
    elif line.strip() == '' or 'And for the 1B model:' in line or 'Core tasks average' in line:
        # Stop at averages or 1B section
        if 'Core tasks average' in line:
            i += 1  # Include this line but stop after
        else:
            break
    else:
        i += 1

print('Total table lines:', len(table_lines))
for j, line in enumerate(table_lines):
    print(f'Line {j}: {repr(line)}')

# Parse headers - remove first empty column
headers = [h.strip() for h in table_lines[0].split('|')[1:-1] if h.strip()]
print('Headers:', repr(headers))

# Find OLMo column index
olmo_col = None
for idx, header in enumerate(headers):
    if 'OLMo 7B' in header and '(ours)' in header:
        olmo_col = idx
        break

print('OLMo column:', olmo_col)

if olmo_col is None:
    print('OLMo column not found')
    exit(1)

# Benchmark mapping with more flexible patterns
benchmark_map = {
    'arc_challenge': 'arc_challenge',
    'arc_easy': 'arc_easy', 
    'boolq': 'boolq',
    'copa': 'copa',
    'hellaswag': 'hellaswag',
    'openbookqa': 'openbookqa',
    'piqa': 'piqa',
    'sciq': 'sciq',
    'winogrande': 'winogrande',
    'truthfulqa': 'truthfulqa',
    'mmlu': 'mmlu'
}

results = []
# Parse data rows - skip header and separator line
for row_idx in range(2, len(table_lines)):
    row_line = table_lines[row_idx]
    cells = [c.strip() for c in row_line.split('|')[1:-1] if c.strip()]

    if len(cells) != len(headers):
        print(f'Skipping row {row_idx} - cell count mismatch: {len(cells)} vs {len(headers)}')
        print(f'Cells: {cells}')
        continue

    benchmark_raw = cells[0]
    if not benchmark_raw or benchmark_raw.lower() in ['core tasks average', 'full average', 'gsm8k']:
        print(f'Skipping average/special row: {benchmark_raw}')
        continue

    benchmark_lower = benchmark_raw.lower()

    # Find matching benchmark
    matched_benchmark = None
    for pattern, benchmark_id in benchmark_map.items():
        if pattern in benchmark_lower:
            matched_benchmark = benchmark_id
            break

    if matched_benchmark:
        score_str = cells[olmo_col]
        print(f'Processing {benchmark_raw}: score_str = \"{score_str}\"')
        # Clean the score - handle bold, spaces, etc.
        score_clean = re.sub(r'[^\d.]', '', score_str)
        if score_clean and len(score_clean) > 0 and score_clean.replace('.', '').isdigit():
            try:
                score = float(score_clean)
                results.append({
                    'name': matched_benchmark,
                    'type': matched_benchmark,
                    'value': score
                })
                print(f'  -> Extracted: {matched_benchmark} = {score}')
            except ValueError as e:
                print(f'  -> Failed to convert score: {score_clean} from {score_str} - {e}')
        else:
            print(f'  -> Invalid score format: {score_str} -> {score_clean}')
    else:
        print(f'No match for benchmark: {benchmark_raw}')

print('\\nExtracted results:')
for r in results:
    print(f'  {r[\"name\"]}: {r[\"value\"]}')

if not results:
    print('No benchmarks extracted!')

# Create YAML structure
yaml_data = {
    'model-index': [{
        'name': 'OLMo 7B',
        'results': [{
            'task': {
                'type': 'text-generation'
            },
            'dataset': {
                'name': 'Core Benchmarks',
                'type': 'benchmark'
            },
            'metrics': results,
            'source': {
                'name': 'OLMo 7B Model Card',
                'url': 'https://huggingface.co/allenai/OLMo-7B'
            }
        }]
    }]
}

# Save to file
with open('olmo_7b_evaluations.yaml', 'w', encoding='utf-8') as f:
    yaml.dump(yaml_data, f, default_flow_style=False, allow_unicode=False, sort_keys=False)

print('\\nYAML file created successfully')
with open('olmo_7b_evaluations.yaml', 'r', encoding='utf-8') as f:
    print(f.read())
"
