python -c "
import yaml
import re

# Hardcoded evaluation data from OLMo 7B README table
evaluations = {
    'arc_challenge': 48.5,
    'arc_easy': 65.4,
    'boolq': 73.4,
    'copa': 90.0,
    'hellaswag': 76.4,
    'openbookqa': 50.2,
    'piqa': 78.4,
    'sciq': 93.8,
    'winogrande': 67.9,
    'mmlu': 28.3,
    'truthfulqa': 36.0
}

# Filter to required benchmarks
required_benchmarks = [
    'arc_challenge', 'arc_easy', 'boolq', 'copa', 'hellaswag', 
    'openbookqa', 'piqa', 'sciq', 'winogrande', 'mmlu', 'truthfulqa'
]

filtered_evals = {k: v for k, v in evaluations.items() if k in required_benchmarks}

# Create model-index YAML structure
model_index = {
    'model-index': [{
        'name': 'OLMo 7B',
        'results': [{
            'task': {
                'type': 'text-generation'
            },
            'dataset': {
                'name': 'OLMo Evaluation Benchmarks',
                'type': 'benchmark'
            },
            'metrics': [
                {
                    'name': benchmark.replace('_', ' ').title(),
                    'type': benchmark,
                    'value': score,
                    'value_formatted': f'{score}%'
                }
                for benchmark, score in filtered_evals.items()
            ],
            'source': {
                'name': 'OLMo 7B Model Card',
                'url': 'https://huggingface.co/allenai/OLMo-7B'
            }
        }]
    }]
}

# Save to YAML file
with open('olmo_7b_evaluations.yaml', 'w') as f:
    yaml.dump(model_index, f, default_flow_style=False, sort_keys=False)

print('Evaluation data saved to olmo_7b_evaluations.yaml')
print(f'Extracted {len(filtered_evals)} benchmarks:')
for benchmark, score in filtered_evals.items():
    print(f'  {benchmark}: {score}%')
"
