import yaml

raw = {
  'model-index': [
    {
      'name': 'OLMo-7B',
      'results': [
        {
          'task': {'type': 'text-generation'},
          'dataset': {'name': 'Benchmarks', 'type': 'benchmark'},
          'metrics': [
            {'name': 'arc_challenge ([Llama 7B](https://arxiv.org/abs/2302.13971))', 'type': 'arc_challenge', 'value': 44.5},
            {'name': 'arc_easy ([Llama 7B](https://arxiv.org/abs/2302.13971))', 'type': 'arc_easy', 'value': 57.0},
            {'name': 'boolq ([Llama 7B](https://arxiv.org/abs/2302.13971))', 'type': 'boolq', 'value': 73.1},
            {'name': 'copa ([Llama 7B](https://arxiv.org/abs/2302.13971))', 'type': 'copa', 'value': 85.0},
            {'name': 'hellaswag ([Llama 7B](https://arxiv.org/abs/2302.13971))', 'type': 'hellaswag', 'value': 74.5},
            {'name': 'openbookqa ([Llama 7B](https://arxiv.org/abs/2302.13971))', 'type': 'openbookqa', 'value': 49.8},
            {'name': 'piqa ([Llama 7B](https://arxiv.org/abs/2302.13971))', 'type': 'piqa', 'value': 76.3},
            {'name': 'sciq ([Llama 7B](https://arxiv.org/abs/2302.13971))', 'type': 'sciq', 'value': 89.5},
            {'name': 'winogrande ([Llama 7B](https://arxiv.org/abs/2302.13971))', 'type': 'winogrande', 'value': 68.2},
            {'name': 'truthfulQA (MC2) ([Llama 7B](https://arxiv.org/abs/2302.13971))', 'type': 'truthfulqa_(mc2)', 'value': 33.9},
            {'name': 'MMLU (5 shot MC) ([Llama 7B](https://arxiv.org/abs/2302.13971))', 'type': 'mmlu_(5_shot_mc)', 'value': 31.5},
          ],
          'source': {
            'name': 'Model README',
            'url': 'https://huggingface.co/allenai/OLMo-7B'
          }
        }
      ]
    }
  ]
}

allowed_types = {
  'arc_challenge', 'arc_easy', 'boolq', 'copa', 'hellaswag',
  'openbookqa', 'piqa', 'sciq', 'winogrande', 'mmlu', 'truthfulqa'
}

# Filter and clean metrics
for model in raw['model-index']:
  for res in model['results']:
    filtered = []
    for m in res['metrics']:
      t = m['type']
      # Normalize some known variants
      if t == 'truthfulqa_(mc2)':
        t_norm = 'truthfulqa'
      elif t == 'mmlu_(5_shot_mc)':
        t_norm = 'mmlu'
      else:
        t_norm = t
      if t_norm in allowed_types:
        # Simplify name to bare benchmark identifier
        simple_name = t_norm.upper() if t_norm in {'mmlu', 'piqa'} else t_norm
        filtered.append({'name': simple_name, 'type': t_norm, 'value': m['value']})
    res['metrics'] = filtered

with open('olmo_7b_evaluations.yaml', 'w') as f:
  yaml.safe_dump(raw, f, sort_keys=False)

PY
