import yaml

# Extract evaluation data from the correct column (last column - OLMo 7B scores)
eval_data = {
    'arc_challenge': 48.5,
    'arc_easy': 65.4,
    'boolq': 73.4,
    'copa': 90.0,
    'hellaswag': 76.4,
    'openbookqa': 50.2,
    'piqa': 78.4,
    'sciq': 93.8,
    'winogrande': 67.9,
    'truthfulqa': 36.0,
    'mmlu': 28.3
}

# Create model-index YAML structure
model_index = {
    'model-index': [{
        'name': 'allenai/OLMo-7B',
        'results': []
    }]
}

# Add results for each benchmark
for benchmark, score in eval_data.items():
    result = {
        'task': {
            'type': 'text-generation',
            'name': benchmark
        },
        'dataset': {
            'type': benchmark,
            'name': benchmark,
            'config': 'default'
        },
        'metrics': [{
            'type': 'accuracy',
            'value': score,
            'name': 'accuracy'
        }],
        'source': {
            'name': 'Model README',
            'url': 'https://huggingface.co/allenai/OLMo-7B'
        }
    }
    model_index['model-index'][0]['results'].append(result)

# Save to YAML file
with open('olmo_7b_evaluations.yaml', 'w') as f:
    yaml.dump(model_index, f, default_flow_style=False, sort_keys=False)

print("Extracted evaluation data:")
for benchmark, score in eval_data.items():
    print(f"{benchmark}: {score}")

print("\nSaved to olmo_7b_evaluations.yaml")
